create database FitnessTrackerdb;
use FitnessTrackerdb;
create table Diet (
    id INT(11) AUTO_INCREMENT,
	mealId INT,
	mealType  varchar(255),
	foodName  varchar(255),
	foodType  varchar(255),
	foodCategory varchar(255),
	readyTime INT,
	calories double,
	protein double,
	fat double,
	carbohydrates double,
	vitaminA double,
	vitaminC double,
	calcium double,
	iron double,
	author varchar(255),
	primary key(id)
);
